Ext.Loader.setConfig({
    enabled: true
});

Ext.application({
    name: 'GUI',
    appFolder: 'gui/mainWindow',

    controllers: [
		'schedule.Schedule',
		'homepage.loginpage',
		'homepage.authorized',
        'Discussions',
		'DiscussionsThread',
        'IM',
        'Main'
    ],

    launch: function() {
        Ext.create('Ext.container.Viewport', {

            layout: {
                type: 'border',
                padding: 5
            },
            defaults: {
                split: true
            },
            items: [
                {
                    region: 'north',
                    title: 'Calgary Emergency Medicine'
                },
                {
                    xtype: 'mainpanel',
                    region: 'center',
                    border: false
                },
                {
                    xtype: 'impanel',
                    region: 'east',
                    collapsible: true
                }
            ]
        });
    }
});